$(document).ready(function() {
	$(".lang-btn").click(function() {
		$('.lang-btn').toggleClass('active');
		$('.lang-item').slideToggle('slow');
	});
	$(".mobilMenu").click(function() {
		$('.mobilMenu').toggleClass('open');
		$('.navMenu').slideToggle('slow');
	});
	$(".mobilMenuUser").click(function() {
		$('.mobilMenuUser').toggleClass('open');
		$('.navuser').slideToggle('slow');
	});
	$( ".faq-title" ).click(function() {
		$(this).toggleClass('open');
		$(this).next().slideToggle('slow');
	});
	$( ".openDetails" ).click(function() {
		// $(this).next().slideUp();
		$(this).toggleClass('open');
		$(this).parent().parent().toggleClass('open');
		if ( $(this).parent().parent().next().is( ":hidden" ) ) {
			$(this).parent().parent().next().slideDown( "slow" );
		} else {
			$(this).parent().parent().next().slideUp();
		}
	});
});

function copyAddress(el) {
	el = (typeof el === 'string') ? document.querySelector(el) : el;
	if (navigator.userAgent.match(/ipad|ipod|iphone/i)) {
		var editable = el.contentEditable;
		var readOnly = el.readOnly;

		el.contentEditable = true;
		el.readOnly = true;

		var range = document.createRange();
		range.selectNodeContents(el);

		var selection = window.getSelection();
		selection.removeAllRanges();
		selection.addRange(range);
		el.setSelectionRange(0, 999999);

		el.contentEditable = editable;
		el.readOnly = readOnly;
		document.getElementById('copyAddress').innerHTML = "Address Copied";
		setTimeout(function() { document.getElementById('copyAddress').innerHTML = "Copy Address"; }, 10000);
	}
	else {
		el.select();
		document.getElementById('copyAddress').innerHTML = "Address Copied";
		setTimeout(function() { document.getElementById('copyAddress').innerHTML = "Copy Address"; }, 10000);
	}
	document.execCommand('copy');
}

